import { gql, useQuery, NetworkStatus } from "@apollo/client";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from "recharts";

export const UNISWAP_QUERY = gql`
  query {
    tokens(
      orderBy: txCount
      orderDirection: desc
      first: 1000
      where: { txCount_gt: 3000 }
    ) {
      id
      symbol
      name
      tradeVolume
    }
  }
`;

export const uniswapQueryVars = {
  first: 10,
};

export default function UniswapList() {
  const { loading, error, data, fetchMore, networkStatus } = useQuery(
    UNISWAP_QUERY,
    {
      variables: uniswapQueryVars,
      // Setting this value to true will make the component rerender when
      // the "networkStatus" changes, so we are able to know if it is fetching
      // more data
      notifyOnNetworkStatusChange: true,
      context: { clientName: "uniswap" },
    }
  );

  const loadingMorePosts = networkStatus === NetworkStatus.fetchMore;

  const loadMorePosts = () => {
    fetchMore({
      variables: {
        skip: tokens.length,
      },
    });
  };

  if (error) return <p> Error</p>;
  if (loading && !loadingMorePosts) return <div>Loading</div>;

  const { tokens } = data;
  <BarChart
    width={500}
    height={300}
    data={data}
    margin={{
      top: 5,
      right: 30,
      left: 20,
      bottom: 5,
    }}
  >
    <CartesianGrid strokeDasharray="3 3" />
    <XAxis dataKey="name" />
    <YAxis />
    <Tooltip />
    <Legend />
    <Bar dataKey="pv" fill="#8884d8" />
    <Bar dataKey="uv" fill="#82ca9d" />
  </BarChart>;
  let tokenNameMap = tokens.map((t) => t.name);
  return (
    <section>
      <h1>Most Swapped Tokens on Uniswap</h1>
      <div>
        <BarChart
          width={900}
          height={300}
          data={tokens}
          margin={{
            top: 5,
            right: 30,
            left: 20,
            bottom: 5,
          }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey={tokens} />
          <YAxis />
          {tokens.map((token) => (
            <>
              <Bar key={token.id} dataKey={token.name} fill="#82ca9d" />
              <Tooltip />
            </>
          ))}
        </BarChart>
      </div>
      <ol>
        {tokens.map((token, index) => (
          <li key={index}>
            Token Name: <strong>{token.name}</strong> Total Trade Volume:{" "}
            {token.tradeVolume}
          </li>
        ))}
      </ol>
    </section>
  );
}
