  //     <Area
  //       type="monotone"
  //       dataKey="tokensCount"
  //       stroke="#9A00D7"
  //       fill="#CF86FA"
  //       strokeWidth={2}
  //     />
  //     <Tooltip />
  //   </AreaChart>
  // );

  return (
    <section>
      <h1>Kyber tokens</h1>
      <ol>
        {reserves.map((trades, index) => (
          <li>
            {tradesAmount}
          </li>
        ))}
      </ol>
    </section>
  );
}

