import { gql, useQuery, NetworkStatus } from "@apollo/client";
import {
  AreaChart,
  Area,
  CartesianGrid,
  XAxis,
  YAxis,
  Tooltip,
} from "recharts";

export const BALANCER_QUERY = gql`
  query {
    pools(first: 1000, orderBy: totalSwapVolume, orderDirection: desc) {
      tokens {
        name
        symbol
        balance
      }
    }
  }
`;

export const balancerQueryVars = {
  first: 10,
};

export default function BalancerList() {
  const { loading, error, data, fetchMore, networkStatus } = useQuery(
    BALANCER_QUERY,
    {
      variables: balancerQueryVars,
      // Setting this value to true will make the component rerender when
      // the "networkStatus" changes, so we are able to know if it is fetching
      // more data
      notifyOnNetworkStatusChange: true,
      context: { dataSrc: "kyber" },
    }
  );

  const loadingMorePosts = networkStatus === NetworkStatus.fetchMore;

  const loadMorePosts = () => {
    fetchMore({
      variables: {
        skip: pools.length,
      },
    });
  };

  if (error) return <p> Error</p>;
  if (loading && !loadingMorePosts) return <div>Loading</div>;

  const { pools } = data;

  // const renderAreaChart = (
  //   <AreaChart width={850} height={600} data={pools}>
  //     <CartesianGrid stroke="#ccc" strokeDasharray="3 3" />
  //     <YAxis domain={["10000000", "dataMax"]} dataKey="poolsCount" />
  //     <XAxis domain={["dataMin", "dataMax"]} />
  //     <Area
  //       type="monotone"
  //       dataKey="poolsCount"
  //       stroke="#9A00D7"
  //       fill="#CF86FA"
  //       strokeWidth={2}
  //     />
  //     <Tooltip />
  //   </AreaChart>
  // );

  return (
    <section>
      <h1>Balancer Pools</h1>
      <ol>
        {pools.map((pool, index) => (
          <li key={index}>
            Pool: {index}, total weight: {pool.totalWeight}

          </li>
        ))}
      </ol>
    </section>
  );
}

