import { gql, useQuery, NetworkStatus } from "@apollo/client";
import {
  BarChart, Bar, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
} from 'recharts';

export const UNISWAP_QUERY = gql`
  query {
    tokens(
      orderBy: txCount
      orderDirection: desc
      first: 1000
      where: { txCount_gt: 3000 }
    ) {
      id
      symbol
      name
      tradeVolume
    }
  }
`;

export const uniswapQueryVars = {
  first: 10,
};

export default function UniswapList() {
  const { loading, error, data, fetchMore, networkStatus } = useQuery(
    UNISWAP_QUERY,
    {
      variables: uniswapQueryVars,
      // Setting this value to true will make the component rerender when
      // the "networkStatus" changes, so we are able to know if it is fetching
      // more data
      notifyOnNetworkStatusChange: true,
      context: { clientName: "uniswap" },
    }
  );

  const loadingMorePosts = networkStatus === NetworkStatus.fetchMore;

  const loadMorePosts = () => {
    fetchMore({
      variables: {
        skip: tokens.length,
      },
    });
  };

  if (error) return <p> Error</p>;
  if (loading && !loadingMorePosts) return <div>Loading</div>;

  const { tokens } = data;

  return (
    <section>
      <h1>Most Swapped Tokens on Uniswap</h1>
      <ol>
        {tokens.map((token, index) => (
          <li key={index}>
            Token Name: <strong>{token.name}</strong> Total Trade Volume:{" "}
            {token.tradeVolume}
          </li>
        ))}
      </ol>
    </section>
  );
}
