import { Card, Button, Badge } from "antd";
import styles from "./defiTile.module.css";

export const DefiTile = ({ imgSrc, handleClick, isCardSelected }) => {
  return (
    <Card
      hoverable
      actions={[<Button onClick={handleClick}>Add to Data Bundle</Button>]}
      className={styles.defiTile}
      style={
        isCardSelected ? { filter: "drop-shadow(16px 16px 20px blue)" } : null
      }
    >
      <img src={imgSrc}></img>
    </Card>
  );
};

export default DefiTile;
