import { gql, useQuery, NetworkStatus } from "@apollo/client";
import { Table } from "./antd";

export const BALANCER_QUERY = gql`
  query {
    pools(first: 10, orderBy: swapsCount, orderDirection: desc) {
      id
      totalWeight
      tokens {
        id
        name
        symbol
        balance
      }
    }
  }
`;

export const balancerQueryVars = {
  first: 10,
};

export default function BalancerList() {
  const { loading, error, data, fetchMore, networkStatus } = useQuery(
    BALANCER_QUERY,
    {
      variables: balancerQueryVars,
      // Setting this value to true will make the component rerender when
      // the "networkStatus" changes, so we are able to know if it is fetching
      // more data
      notifyOnNetworkStatusChange: true,
      context: { clientName: "balancer" },
    }
  );

  // const loadingMorePosts = networkStatus === NetworkStatus.fetchMore;

  // const loadMorePosts = () => {
  //   fetchMore({
  //     variables: {
  //       skip: pools.length,
  //     },
  //   });
  // };

  if (error) return <p> Error</p>;
  if (loading && !loadingMorePosts) return <div>Loading</div>;

  const { pools } = data;
  let poolsList = pools.map((pool, index) => {
    let tokens = pool.tokens;
    return (
      <div key={index}>
        <h1>Pool {index}</h1>
        {tokens.map((token) => (
          <p key={token.id}>{token.name}</p>
        ))}
      </div>
    );
  });

  console.log(poolsList);

  const columns = [
    {
      title: "Pool",
      dataIndex: "pool",
    },
  ];

  return (
    <section>
      <h1>What the biggest Balancer Pools are holding</h1>
      {/* <>{poolsList}</> */}
      <Table
        columns={columns}
        dataSource={pools}
        pagination={true}
        style={{ width: "100%" }}
      />
    </section>
  );
}
